import React, { Component } from 'react';
import { Media } from 'reactstrap';
import { Card, CardImg, CardImgOverlay, CardText, CardBody,
    CardTitle } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
class DishDetail extends Component{
	    constructor(props) {
        super(props);
        this.state = {
            dish: this.props.dish
        }
console.log(this.state.dish)
    }
    onDishSelect(dish) {
        this.setState({ selectedDish: dish});
    }
	renderComments(dish){
		if (dish != null)
		{
			const comments = dish.comments.map((com) => {
				return (
				  <div  className="list-unstyled">
					<div>{com.comment}</div>
					<div>
						{com.author} 
						{new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(com.date)))}
					</div>
				  </div>
				);
			});
			return(
				<Card>
						<CardBody>
						  <h4>Comments</h4>
						  <CardText>{comments}</CardText>
						</CardBody>
					</Card>
			)
		}
		else
		{
			return(
					<div></div>
				);
		}
	}
	renderDish(dish) {
			if (dish != null)
				return(
					<Card key={dish.id}
					  onClick={() => this.onDishSelect(dish)}>
					   <CardImg width="100%" src={dish.image} alt={dish.name} />
					  <CardImgOverlay>
						  <CardTitle>{dish.name}</CardTitle>
					  </CardImgOverlay>
					</Card>
					);
				else
					return(
						<div></div>
					);
		}
	render(){
		const Sdish=this.state.dish
		 if (Sdish != null)
		{
			return(
                <div className="row">
                  <div  className="col-12 col-md-5 m-1">
                    {this.renderDish(Sdish)}
                  </div>
                  <div  className="col-12 col-md-5 m-1">
                    {this.renderComments(Sdish)}
                  </div>
                </div>
			)
		}
		else
		{
			return (<div></div>)
		}
	}
	
}
export default DishDetail;
